import { IRouter } from 'express';
import { IUnleashConfig } from '../types/option';
/**
 * Base class for Controllers to standardize binding to express Router.
 */
export default class Controller {
    app: IRouter;
    config: IUnleashConfig;
    constructor(config: IUnleashConfig);
    get(path: string, handler: Function, permission?: string): void;
    post(path: string, handler: Function, permission?: string, ...acceptedContentTypes: string[]): void;
    put(path: string, handler: Function, permission?: string, ...acceptedContentTypes: string[]): void;
    delete(path: string, handler: Function, permission?: string): void;
    fileupload(path: string, filehandler: Function, handler: Function, permission?: string): void;
    use(path: string, router: IRouter): void;
    get router(): any;
}
